import CanvasDraw from 'react-canvas-draw'

function App(props) {

  props = {
    color: 'white',
  }

  return (
    <div className="container">
        <h1>
          Welcome to Quick Draw!
        </h1>
        <CanvasDraw 
          className='canvas'
          onChange={() => props.color = "#" + Math.floor(Math.random() * 16777215).toString(16)}
          brushColor={props.color}
          hideGrid={true}
          brushRadius={2}
          lazyRadius={0}
        />
    </div>
  );
}

export default App;
