import CanvasDraw from 'react-canvas-draw'

function App({color}) {

  color = 'black';

  return (
    <div className="container">
        <h1>
          Welcome to Quick Draw!
        </h1>
        <CanvasDraw 
          className='canvas'
          ref={canvasDraw => (this.saveableCanvas = canvasDraw)}
          onChange={() => color = "#" + Math.floor(Math.random() * 16777215).toString(16)}
          brushColor={color}
          hideGrid={true}
          brushRadius={2}
          lazyRadius={0}
        />
        
    </div>
  );
}

export default App;
