import CanvasDraw from 'react-canvas-draw'
import {useState} from 'react'
import axios from 'axios'

function sendData(data) {
  axios.post('http://localhost:8000/data/', {
    lines: data
  }, {
    headers: {
      'Content-Type': 'application/json',
    },
  }).then(res => {
    console.log(res['data']);
    return res['data'];
  })
}

function App() {

  const [color, setColor] = useState('black')
  const [saveableCanvas, setsaveableCanvas] = useState(null)
  const [list, setList] = useState([
    {
      name: 'No prediction', 
      confidence: '0',
    }])
  
  return (
    <div className="container">
        <h1>
          Welcome to Quick Draw!
        </h1>
        <div className='details'>
          <CanvasDraw 
            className='canvas'
            ref={canvasDraw => setsaveableCanvas(canvasDraw)}
            onChange={() => setColor("#" + Math.floor(Math.random() * 16777215).toString(16))}
            brushColor={color}
            hideGrid={true}
            brushRadius={2}
            lazyRadius={0}
          />
          <ol style={{color: 'white'}}>
            {list.map((item) => (
              <li key={item.name}>
                {item.name}
                {/* {item.confidence} */}
              </li>
            ))}
          </ol>
        </div>
        <div className='button'>
          <button onClick={() => setList(sendData(saveableCanvas.getSaveData()))}>
            Predict Doodle
          </button>
          <button onClick={() => saveableCanvas.clear()}>
            Clear Screen
          </button>
        </div>

    </div>
  );
}

export default App;
